		this.clearMoves();
		const from = this.continuedMove;
		this.getMoves(from);
		const pi = this.board[from] >> 5;
		const to = this.moves[pi][Math.floor(Math.random() * this.moves[pi].length)] & (BIT_AREA - 1);
