import socket from 'socket.io';
import Leap from '../assets/leap';
import { EVENT, CONNECTION, DISCONNECT } from '../constants/eventTypes';
import { USER, LOBBY, ROOM, GAME, CHAT } from '../../common/constants/';


const onlineUsers = {};
const rooms = {
  'room1': {
    game: new Leap (4,8,0),
    whitelist: false,
    users: [],
    host: 'user1',
    player: {
      one: '',
      two: '',
    }

  },
  'room2': {
    game: new Leap (4,8,0),
    whitelist: false,
    users: [],
    host: 'user2',
    player: {
      one: '',
      two: '',
    }
  },
};
