import socketIOClient from 'socket.io-client';
//import store from '../store/';
import { ENDPOINT } from '../constants/socket.types';
import { USER, LOBBY, ROOM, GAME, CHAT } from '../constants/';
import ACTION from '../actions';


const socketMiddleware = () => {
  let io = null;

  const onConnect = store => event => {
    console.log('Howdy!');
  };

  const onDisconnect = store => event => {
    console.log('Bowdy?');
  };

  const onMessage = dispatch => action => {
    //TODO: I guess this is where we handle the server data
    switch (action.type) {
      case LOBBY.UPDATE:
        dispatch({type: ROOM.FETCH_ROOMS, payload: action.payload});
        //dispatch(action);
        break;
      default: break;
    }
  };

  return dispatch => action => {
    console.log(dispatch);
    switch (action.type) {
      case 'connect':
        if (io) break;

        io = socketIOClient(action.host);
        io
          .on('connect', onConnect(dispatch))
          .on('message', onMessage(dispatch))
          .on('disconnect', onDisconnect(dispatch));
        break;

      case 'disconnect':
        if (io) io.close();
        io = null;
        console.log('Disconnected.');
        break;

      case USER.LOGIN:
      case USER.LOGOUT:
        io.emit('userEvent', action);
        dispatch(ACTION.USER.userLogin(action.payload));
        break;

      case LOBBY.ADD_ROOM:
      case LOBBY.REMOVE_ROOM:
      case LOBBY.UPDATE:
        io.emit('lobbyEvent', action);
        break;

      case ROOM.ENTER:
      case ROOM.EXIT:
      case ROOM.SUBMIT_MOVE:
      case ROOM.END_GAME:
        io.emit('roomEvent', action);
        break;

      case GAME.START:
      case GAME.SELECT:
      case GAME.END:
        io.emit('gameEvent', action);
        break;


      default:
      return dispatch(action);
    }
  };
};

