import LOBBY from '../constants/lobby.types';

const initialState = {
  views: {
    'home': {
      rooms: ['aaa'], //room ids contained here
      activeRoomID: 0,
    },
    'browse': {
      rooms: ['aaa'],
      activeRoomID: 0,
    },
    'leaderboard': {

    }
  },
};

function lobbyReducer (state = initialState, action) {
  switch (action.type) {
    case LOBBY.ADD_ROOM:
      break;
    case LOBBY.REMOVE_ROOM:
      break;
    case LOBBY.UPDATE:
      break;
    case LOBBY.TOGGLE_SCROLL:
      break;
    default:
      break;
  }
}

export default lobbyReducer;
