import React, { useState } from 'react';
import socketIOClient from "socket.io-client";
import crypto from 'crypto';
import './css/App.css';
import './lib/fa/css/all.min.css';
import { CSSTransition } from 'react-transition-group';
import Settings from './Settings';
import Leap from './Leap';
import Menu from './Menu';

const CONFIG = {
  id: '',
  difficulty: 0,
  online: true,
  port: 3001,
  player: {},
  size: 8,
};

class App extends React.Component {
  // add back   <Menu/>
  //If roomURL isn't empty, then Multiplayer
  //go to roomURL.
  constructor () {
    super();
    this.socket = socketIOClient(`http://localhost:${CONFIG.port}`);
    var name;
    do { name = prompt("username?");}while(!name);
    const player = {
      name: name,
      bot: false,
      color: 'white',
      first: true,
    };
    CONFIG.player = player;
    this.socket.on('connect', ()=> {
      this.socket.emit('login', {player});
    });

    this.state = {
      player: player,
      inGame: '',
      lobby: [],
    };
  }

  enterGame(id) {
    this.setState({inGame: id});
    CONFIG.id = id;
  }

  componentDidMount () {
    let lobby = [];
    this.socket.on('gamesSent', games => {
      for (let game in games) {
        lobby.push(
          <button
            onClick={ () => enterGame(game.id) }>
            { `Game with ${game.users}`}
          </button>
        );
    }});

    this.setState({lobby: lobby});
  }



  exitGame() {

  }

  createGame () {
    this.socket.emit('gameCreate', {
      id: crypto.randomBytes(16, (err, buffer)=> { buffer.toString('hex') }),
      whitelist: false,
      host: this.state.player.name,
      users : {},
    });
  }

  render () {

    this.socket.emit('sendGames');

    return (
      <div className="App">
        <Settings/>
        <div>
          gameroonis
          { this.state.lobby }
        </div>
        <button
          className='start-game-btn'
          onClick={ () => createGame() }>
          Create new game
        </button>
        { this.state.inGame && <Leap config={ CONFIG }/> }
      </div>
    );
  }
}



export default App;
