  //     this.setState({activeNavItem: nextProps.activeNavItem});
  //     return true;
  //   }
  // }

  render() { return <ModalContent type={ this.state.activeNavItem }/>; }
}

function ModalContent(props){
  const modalType = {
    'stats':    <Playmode/>,
    'create':   <Playmode/>,
    'friends':  <Playmode/>
  };

  return (
    <div className='nav-modal'>
     { modalType[this.state.type] }
    </div>
  );
}
