import React, { useState } from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { bool, array, func, string, number, object } from 'prop-types';
import { getMe, getIsLoggedIn } from '../../selectors/';
import { userLogin } from '../../actions/user';

import ViewStack from '../ViewStack/';
import Room from '../Room/';
import BottomNavigation from '../BottomNavigation/';
import './index.css';
//import './lib/fa/css/all.min.css';

class App extends React.Component {
  constructor({ socket, me, isLoggedIn, userLogin}) {
    super();
    this.me = me;
    this.socket = socket;
  }

  componentDidMount() {
    userLogin({me: this.me, io: this.socket.io});
  }

  //const ClientSocket = React.createContext();
  render () {
    return (
      <div className="App">
        <ViewStack/>
      </div>
    );
  }
}
//{!isLoggedIn && <Splash/>}
//<BannerContainer />
//<HeaderContainer me={me}/> //has settings at upper righthand
//<CreateGameModal io={io}/>
//<BottomNavigation/>

App.propTypes = {
  me: object.isRequired,
  isLoggedIn: bool.isRequired,
}
//
const actions = {
    userLogin,
};

const mapDispatchToProps = dispatch => ({userLogin: () => dispatch(userLogin())});//bindActionCreators(actions, dispatch);

const mapStateToProps = state => ({
  me: getMe(state),
  isLoggedIn: getIsLoggedIn(state),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
