import React, { useState } from 'react';
import socketIOClient from "socket.io-client";
import './css/App.css';
import './lib/fa/css/all.min.css';
import { CSSTransition } from 'react-transition-group';
import Settings from './Settings';
import Leap from './Leap';
import Menu from './Menu';

const CONFIG = {
  id: '12345',
  difficulty: 0,
  online: true,
  port: 3001,
  player: {},
  size: 8,
};

class App extends React.Component {
  // add back   <Menu/>
  //If roomURL isn't empty, then Multiplayer
  //go to roomURL.
  constructor () {
    super();
    this.socket = socketIOClient(`http://localhost:${CONFIG.port}`);
    var name;
    do { name = prompt("username?");}while(!name);
    const player = {
      name: name,
      bot: false,
      color: 'white',
      first: true,
    };
    CONFIG.player = player;
    this.socket.on('connect', ()=> {
      this.socket.emit('login', {player});
    });

    this.state = {
      player: player,
      inGame: '',
    };
  }

  componentDidMount () {
    //make new process on server to set CONFIG.port. right now it's static

  }

  test() {
    this.socket.emit('gameCreate', {
      id: '12345',
      whitelist: false,
      users : {},
    });
  }

  render () {
    return (
      <div className="App">
      <Settings/>
      <button /*TODO: only appear when required input satisfied (sp: just click the playmode, mp: select one of three options)*/
        className='start-game-btn'
        onClick={ () => test() }>
        Create new game
      </button>
      <Leap config={ CONFIG }/>
      </div>);
  }
}



export default App;
