import React from 'react';

class NavigationModal extends React.Component {
  constructor(props){
    super(props);
    this.state = { nav: props.nav };
  }
  render() {
    let modal;
    switch(this.state.nav) {
      case 'stats':
        modal = <ModalContent type='stats'/>;
        break;
      case 'create':
        modal = <ModalContent type='create'/>;  // need activeItem for = single/multiplayer
        break;
      case 'friends':
        modal = <ModalContent type='friends'/>;
        break;
      default:
        break;

        return modal ? <div class='nav-modal-container'> modal </div> : <div class='nav-modal-container'></div>;
    }
  }
}

