import React from 'react';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { func } from 'prop-types';
import { userLogin } from '../../actions/user';
import { v4 as getUUID } from 'uuid';

class Login extends React.Component {
  constructor ({ userLogin }) {
    super();
    this.state = {name: ''};
    this.userLogin = userLogin.bind(this);
    this.update = this.handleChange.bind(this);
    this.submit = this.handleSubmit.bind(this);
  }
  handleChange (event) { this.setState({name: event.target.value}); }
  handleSubmit (event) { this.userLogin({me: {id: getUUID(), name: this.state.value} }); }

  render () {
    return (
      <form onSubmit={this.submit}>
        <label>
          Display name:
          <input type="text" value={this.state.name} onChange={this.update} />
        </label>
        <input type="submit" value="Done" />
      </form>
    );
  }
};

Login.propTypes = {
  userLogin: func.isRequired,
}

const actions = { userLogin };

const mapDispatchToProps = dispatch => bindActionCreators(actions, dispatch);

export default connect(mapDispatchToProps)(Login);
