import React from 'react';
import { connect } from 'react-redux';
import Room from '../Room';
import crypto from 'crypto';
import { getLobbyRooms, getActiveRoom } from '../../selectors/';
import { lobbyUpdate, lobbyToggleScroll } from '../../actions/lobby';
import { array, string } from 'prop-types';

import './Lobby.css';


const Lobby = ({id, rooms, activeRoom}) => {

  /*
   TODO: add actions
    load Games through up/down arrows + or - state roomIndex
  */
  const size = Object.keys(rooms).length;
  let loadedRooms = [];
  if (size > 1) {
    const prev = Math.abs( (activeRoom-1)%size );
    const next = (activeRoom+1)%size;
    for (let i=-1; i<2; i++) {
      loadedRooms.push( <Room key={i+1} room={rooms[(activeRoom+i)%size].id} active={!i} />);
    }
  } else {
    loadedRooms.push(<Room key={0} room={rooms[activeRoom].id} active={true} />);
  }


  return (
    <div className= "lobby-container">
      { loadedRooms }
    </div>
  );

};

Lobby.propTypes = {
  rooms: array,
  activeRoom: string,
}

//TODO: make selectors
const mapStateToProps = state => ({
  rooms: getLobbyRooms(state),
  activeRoom: getActiveRoom(state),

})

export default connect(mapStateToProps)(Lobby);
