import socketIOClient from 'socket.io-client';
//import store from '../store/';
import { ENDPOINT } from '../constants/socket.types';
import { USER, LOBBY, ROOM, GAME, CHAT } from '../constants/';
import ACTION from '../actions';


const socketMiddleware = () => {
  let socket = null;

  const onConnect = store => event => {
    console.log('Howdy!');
  };

  const onDisconnect = store => event => {
    console.log('Bowdy?');
  };

  const onMessage = dispatch => action => {
    //TODO: I guess this is where we handle the server data
    if (action.type === LOBBY.UPDATE) dispatch(action.payload);
  };

  return dispatch => action => {
    console.log(dispatch);
    switch (action.type) {
      case 'connect':
        if (socket) break;

        socket = socketIOClient(action.host);
        socket
          .on('connect', onConnect(dispatch(action)))
          .on('message', onMessage(dispatch(action)))
          .on('disconnect', onDisconnect(dispatch(action)));
        break;

      case 'disconnect':
        if (socket) socket.close();
        socket = null;
        console.log('Disconnected.');
        break;

      case USER.LOGIN:
      case USER.LOGOUT:
        socket.io.emit('eventUser', action);
        dispatch(ACTION.USER.userLogin(action.payload));
        break;

      case LOBBY.ADD_ROOM:
      case LOBBY.REMOVE_ROOM:
      case LOBBY.UPDATE:
        socket.emit('eventLobby', action);
        break;

      case ROOM.ENTER:
      case ROOM.EXIT:
      case ROOM.SUBMIT_MOVE:
      case ROOM.END_GAME:
        socket.emit('eventRoom', action);
        break;

      case GAME.START:
      case GAME.SELECT:
      case GAME.END:
        socket.emit('eventGame', action);
        break;


      default:
      return dispatch(action);
    }
  };
};

