import React from 'react';
import './css/menu.css';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css'


function NavModalContent(props){
  const modalType = {
    'stats':    <Playmode/>,
    'create':   <Playmode/>,
    'friends':  <Playmode/>
  };

  return (
    <div className='nav-modal'>
     { modalType[props.type] }
    </div>
  );
}
//playmode
//FriendRow

class Playmode extends React.Component {
  constructor(){
    super();
    this.state = { activePlaymode: ''};
  }
  selectedPlayMode(mode) {
    if( (mode !== this.state.activePlaymode) ) this.setState( {activePlaymode: mode} );
  }

  render() {
    switch(this.state.activePlaymode) {
      case 'sp':

        break;
      case 'mp':
        break;
      default:
        break;
      }
        //render. active modes?
        return (<div className='nav-modal-container'>
          <div className='playmode-btn-container'>
            <div className='playmode-sp-btn' onClick={ () => this.selectedPlayMode('sp') }>
              <i className='fas fa-user fa-2x'/>
              Singleplayer
            </div>
            <div className='playmode-mp-btn' onClick={ () => this.selectedPlayMode('mp') }>
              <i className='fas fa-user-friends fa-2x'/>
              Multiplayer
            </div>
          </div>
          <div id='sp' className='playmode-options-container'>
          <Slider dots
            step={10}
            defaultValue={50}
            onAfterChange={console.log('ye')}
            dotStyle={{borderColor: 'blue'}}
            activeDotStyle={{borderColor: 'green'}}/>
          </div>
          <div id='mp' className='playmode-options-container'>
          </div>
          </div>
        );
  }
}

export default NavModalContent;
